def copyFiles(output, fileList):
    if not os.path.isdir(output):
        os.makedirs(output)
    for f in fileList:
        shutil.copyfile(f, os.path.join(output, os.path.basename(f)))


def copyFileTree(output, fileList, pattern):
    regexpr = re.compile(pattern)
    if not os.path.isdir(output):
        os.makedirs(output)
    for f in fileList:
        for root, dirs, files in os.walk(f):
            for name in files:
                path = os.path.join(root, name)
                if re.match(regexpr, path):
                    relPath = os.path.relpath(path, f)
                    dst = os.path.join(output, relPath)
                    dstDirName = os.path.dirname(dst)
                    if not os.path.isdir(dstDirName):
                        os.makedirs(dstDirName)
                    shutil.copyfile(path, dst)


if __name__ == "__main__":
    parser = argparse.ArgumentParser(
        description="Generate the content of the esp32 SDK from esp-idf.")
    parser.add_argument("output", type=str,
                        help="Output directory where to store the results.")

    args = parser.parse_args()

    # Create the output directory
    output = os.path.abspath(os.path.expanduser(args.output))
    if input("Do you want to create and clean '{}'? ".format(output)).lower().strip() == "y":
        if os.path.isdir(output):
            shutil.rmtree(output)
        os.makedirs(output)

    for cmdStr in [COMPILE_COMMAND_LINE, LINKER_COMMAND_LINE]:
        result = tools.scripts.extract_compiler_cmd.parse(cmdStr)

        copyFiles(os.path.join(output, "ld"), result["files"]["lds"])
        copyFiles(os.path.join(output, "lib"), result["files"]["libs"])
        copyFileTree(os.path.join(output, "include"), filter(
            lambda x: x not in ["."], result["files"]["includes"]), r'.*\.h')

        print("Linker commands (lds): {}".format(
            " ".join(["-T{}".format(p) for p in result["linker"]["lds"]])))
        print("Linker commands (libs): {}".format(
            " ".join(["-l{}".format(p) for p in result["linker"]["libs"]])))
